            builder.Entity<AuthorDetail>().HasData(new AuthorDetail() { Id = 1, Biography = "Agatha Christie est sans nul doute l'une des romancières les plus appréciées de son temps. Auteure de quatre-vingt-quatre ouvrages qui constituent pour la plupart des intrigues policières, d'une vingtaine de pièces de théâtre et de plusieurs recueils de nouvelles, elle est parvenue à faire de ses oeuvres de grands succès du XXe siècle, lues partout dans le monde (plus de 2 milliards d'exemplaires vendus).", Photo = "" });
            builder.Entity<AuthorDetail>().HasData(new AuthorDetail() { Id = 2, Biography = "Auteur de plusieurs ouvrages portant sur l’administration, la gestion des ressources humaines, l’informatique et le commerce électronique, Bernard Turgeon a obtenu le Prix du ministre de l’Éducation(1982), deux mentions(1987, 2000) et un prix d’encouragement(1985).Titulaire d’un baccalauréat en administration et d’une licence en sciences commerciales, il enseigne le management et la gestion des ressources humaines au Cégep Édouard - Montpetit", Photo = "" });
            builder.Entity<AuthorDetail>().HasData(new AuthorDetail() { Id = 3, Biography = "Docteur en relations industrielles, Dominique Lamaute enseigne au département des Techniques administratives du Cégep de Saint-Hyacinthe. Auteur d’ouvrages sur le management, la gestion des ressources humaines et l’entreprise, il a obtenu une mention au Prix du ministre de l’Éducation (2000). Spécialisé en gestion stratégique des ressources humaines, Dominique Lamaute est appelé à prononcer plusieurs conférences sur l’évolution des modèles de gestion des ressources humaines et sur le talent management.", Photo = "" });
            builder.Entity<AuthorDetail>().HasData(new AuthorDetail() { Id = 4, Biography = "musicien et enseignant. Il a joué pour Cavalia et dans des groupes de trad, de free jazz et de black métal. 'Le puits' (2020) est son premier roman.", Photo = "" });
            builder.Entity<AuthorDetail>().HasData(new AuthorDetail() { Id = 5, Biography = "Kim Messier enseigne le français à l'école secondaire du Verbe Divin, à Granby, où elle habite. En publiant son premier roman aux Éditions de Mortagne, cette enseignante réalise le plus grand rêve de sa vie ! Le récit de Léa s'est imposé à elle en 2008, lors d'un congé de maternité. Depuis, c'est l'avalanche ! Les idées s'enchaînent, les mots surgissent et Kim ne rêve plus que d'une chose : écrire à temps plein.", Photo = "" });
            builder.Entity<AuthorDetail>().HasData(new AuthorDetail() { Id = 6, Biography = "Né le31 août 1964, est un écrivain québécois spécialisé en fantastique et fantasy. Son premier roman, L'Orbe et le Croissant, est publié par les Éditions Arion en 2006.Les Éditions Porte Bonheur l'ont réédité, puis édité les deux tomes suivants formant La Trilogie de L'Orbe.Il a ensuite écrit une deuxième trilogie L'héritière de Ferrolia en 2010. Auteur prolifique, il a ensuite publié Cœur de givre en avril 2011 et poursuivi avec une incursion dans le monde de la science-fiction en livrant un tome de la série des Clowns vengeurs, Valse macabre avril 2012. Sa dernière œuvre, Les âmes perdues, est parue en novembre 2012.", Photo = "" });
