            builder.Entity<Book>().HasData(new Book() { Id = 11, Title = "Valse macabre", ISBN = "9782898032752", PublishedDate = new DateTime(2019, 09, 17, 00, 00, 00), Price = 20, Available = true, Promo = false, Subject_Id = 2, Publisher_Id =3, Resume = "Le Menvatt tourna une petite clé située sur le côté de la boîte avant de la poser au sol et d’en ouvrir le couvercle. Une musique triste se mit à résonner dans la pièce, et au centre de la boîte, la figurine d’une ballerine entièrement vêtue de blanc tournoyait au bras d’un clown multicolore. Jordan pencha la tête de côté, fredonnant la mélodie. Il se pencha de nouveau au-dessus de son sac, d’où il sortit une paire de pinces robustes au bout pointu. Mercado fronça les sourcils. Les menvatts ne faisaient pas dans la dentelle. Ils tuaient, appliquaient la vengeance sans perdre de temps. Pourquoi alors cette paire de pinces? Dans la Quadri-métropole aux prises avec des luttes politiques, les arcurides du Gouvernement Légitime pourchassent sans relâche les Odi-Menvatts, alors qu’un dangereux psychopathe s’est glissé parmi eux…" });
            builder.Entity<Book>().HasData(new Book() { Id = 12, Title = "Mission Arctik", ISBN = "9782898190063", PublishedDate = new DateTime(2020, 02, 12, 00, 00, 00), Price = 20, Available = true, Promo = true, Subject_Id = 1, Publisher_Id = 3, Resume = "Alain Thibault, éminent conférencier du paranormal, est approché par une mystérieuse organisation pour participer à une expédition dans l'Arctique. Sous la menace des responsables de l'institution, il est contraint d'accepter de partir dans le Grand Nord canadien. Pourquoi la participation d'Alain est-elle essentielle pour les membres de cette mission ? Quels dangers guettent les participants de l'expédition Mirage ? Que cherche cette énigmatique agence au-delà du 82e parallèle ? Lorsque l'expédition découvre une base souterraine construite par les nazis durant la Deuxième Guerre mondiale, Alain comprend qu'on ne lui a vraiment pas tout dit... Un thriller fantastique haletant nous transportant aux confins de l'Arctique, abri sauvage et méconnu de très anciens secrets..." });
